                    'letterFontSize': String(styleLetter['fontSize']),
                    'letterFontColor': String(styleLetter['color']),
                    'letterPaper': String(styleLetter['backgroundImage']),
                    'letterWriter': String(author),
                    'letterIcon': String(stamp)
                },
