    // 사용자 정보 요청 기능
    function requireUserCheckData(props) {
        fetch(`${process.env.REACT_APP_USER_DATA_SHARE}${props}`, {
            method: 'GET',
            mode: 'cors',
            cache: 'no-cache',
            credentials: 'same-origin',
