                setUserNickName(String(userData.userNickName));
                setOpenUserOpenDate(Number(userData.userOpenDate));
                fetch(`${process.env.REACT_APP_LETTER_COUNT_SHARE}${props}`, {
                    method: 'GET',
                    mode: 'cors',
                    cache: 'no-cache',
                    credentials: 'same-origin',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                })
                    .then(res => {
