            let distance = openUserOpenDate - now;
            if (distance >= 0) {
                if (userLetterCount >= 36) {
                    alert('행성이 편지로 가득찼습니다. Pl@ter 페이지로 이동합니다.');
                    // window.location.replace('/main');
